option.value:
import controle.*;
import entidades.*;
import java.time.LocalDate;

public class GeradorDados {
    public static void main(String[] args) {
        System.out.println("Gerando arquivos .dat...");

        try {
            gerarClientes();
            gerarEspacos();
            gerarReservas();
            System.out.println("Arquivos .dat criados com sucesso:");
            System.out.println("- clientes.dat");
            System.out.println("- espacos.dat");
            System.out.println("- reservas.dat");
        } catch (Exception e) {
            System.out.println("Erro: " + e.getMessage());
        }
    }

    private static void gerarClientes() throws Exception {
        RepositorioClientes repo = new RepositorioClientes();
        LocalDate hoje = LocalDate.now();

        // Criando alguns clientes de exemplo
        repo.adicionarCliente(new Cliente("12345678909", "Felipe", "felipe@email.com", "(98) 91234-5678", hoje));
        repo.adicionarCliente(new Cliente("23456789012", "Raimundo", "raimundo@email.com", "(99) 92345-6789", hoje));
        repo.adicionarCliente(new Cliente("34567890123", "Messi", "messi@email.com", "(98) 93456-7890", hoje));
        repo.adicionarCliente(new Cliente("45678901234", "Noé", "noe@email.com", "(99) 94567-8901", hoje));

        repo.salvarArquivo("clientes.dat");
        System.out.println("4 clientes criados");
    }

    private static void gerarEspacos() throws Exception {
        RepositorioEspacos repo = new RepositorioEspacos();

        // Estacoes de Trabalho
        repo.adicionarEspaco(new EstacaoTrabalho("Estacao Messi 1", 15.0));
        repo.adicionarEspaco(new EstacaoTrabalho("Estacao Xavi 2", 15.0));
        repo.adicionarEspaco(new EstacaoTrabalho("Estacao Iniesta 3", 18.0));

        // Salas Privadas
        repo.adicionarEspaco(new SalaPrivada("Sala Privada Alpha", 35.0));
        repo.adicionarEspaco(new SalaPrivada("Sala Privada Cr7", 77.7));
        repo.adicionarEspaco(new SalaPrivada("Sala Privada modric", 45.0));
        repo.adicionarEspaco(new SalaPrivada("Sala Casemiro", 60.0));

        // Salas de Reuniao
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Rai", 50.0));
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Rogerio", 55.0));
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Ceni", 65.0));
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Kaka", 75.0));

        // Auditorios
        repo.adicionarEspaco(new Auditorio("Auditorio Principal", 120.0));
        repo.adicionarEspaco(new Auditorio("Auditorio Mirassol", 100.0));
        repo.adicionarEspaco(new Auditorio("Auditorio SP", 110.0));
        repo.adicionarEspaco(new Auditorio("Auditorio Palmeiras", 150.0));

        repo.salvarArquivo("espacos.dat");
        System.out.println("16 espacos criados de todos os tipos");
    }

    private static void gerarReservas() throws Exception {
        RepositorioReservas repo = new RepositorioReservas();

        // Clientes de exemplo
        Cliente cliente1 = new Cliente("12345678909", "Felipe", "felipe@email.com", "(98) 91234-5678", LocalDate.now());
        Cliente cliente2 = new Cliente("23456789012", "Raimundo", "raimundo@email.com", "(99) 92345-6789", LocalDate.now());

        // Espaços de exemplo - CORRIGIDO: usando espacos reais, nao servicos
        Espaco espaco1 = new EstacaoTrabalho("Estacao Individual 1", 15.0);
        Espaco espaco2 = new EstacaoTrabalho("Estacao Individual 2", 20.0);

        // Criando reservas de exemplo
        Reserva reserva1 = new Reserva(cliente1, espaco1,
                LocalDate.now().plusDays(1),
                java.time.LocalTime.of(9, 0),
                java.time.LocalTime.of(12, 0));

        Reserva reserva2 = new Reserva(cliente2, espaco2,
                LocalDate.now().plusDays(2),
                java.time.LocalTime.of(14, 0),
                java.time.LocalTime.of(18, 0));

        // Adicionando servicos extras nas reservas
        reserva1.adicionarServico(new CafePremium(3, 5.50));
        reserva2.adicionarServico(new Locker(1, 10.0));

        repo.adicionarReserva(reserva1);
        repo.adicionarReserva(reserva2);
        repo.salvarArquivo("reservas.dat");

        System.out.println("2 reservas criadas");
    }

}

import controle.*;
import entidades.*;
import java.time.LocalDate;

public class GeradorDados {
    public static void main(String[] args) {
        System.out.println("Gerando arquivos .dat...");

        try {
            gerarClientes();
            gerarEspacos();
            gerarReservas();
            System.out.println("Arquivos .dat criados com sucesso:");
            System.out.println("- clientes.dat");
            System.out.println("- espacos.dat");
            System.out.println("- reservas.dat");
        } catch (Exception e) {
            System.out.println("Erro: " + e.getMessage());
        }
    }

    private static void gerarClientes() throws Exception {
        RepositorioClientes repo = new RepositorioClientes();
        LocalDate hoje = LocalDate.now();

        // Criando alguns clientes de exemplo
        repo.adicionarCliente(new Cliente("12345678909", "Felipe", "felipe@email.com", "(98) 91234-5678", hoje));
        repo.adicionarCliente(new Cliente("23456789012", "Raimundo", "raimundo@email.com", "(99) 92345-6789", hoje));
        repo.adicionarCliente(new Cliente("34567890123", "Messi", "messi@email.com", "(98) 93456-7890", hoje));
        repo.adicionarCliente(new Cliente("45678901234", "Noé", "noe@email.com", "(99) 94567-8901", hoje));

        repo.salvarArquivo("clientes.dat");
        System.out.println("4 clientes criados");
    }

    private static void gerarEspacos() throws Exception {
        RepositorioEspacos repo = new RepositorioEspacos();

        // Estacoes de Trabalho
        repo.adicionarEspaco(new EstacaoTrabalho("Estacao Messi 1", 15.0));
        repo.adicionarEspaco(new EstacaoTrabalho("Estacao Xavi 2", 15.0));
        repo.adicionarEspaco(new EstacaoTrabalho("Estacao Iniesta 3", 18.0));

        // Salas Privadas
        repo.adicionarEspaco(new SalaPrivada("Sala Privada Alpha", 35.0));
        repo.adicionarEspaco(new SalaPrivada("Sala Privada Cr7", 77.7));
        repo.adicionarEspaco(new SalaPrivada("Sala Privada modric", 45.0));
        repo.adicionarEspaco(new SalaPrivada("Sala Casemiro", 60.0));

        // Salas de Reuniao
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Rai", 50.0));
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Rogerio", 55.0));
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Ceni", 65.0));
        repo.adicionarEspaco(new SalaReuniao("Sala Reuniao Kaka", 75.0));

        // Auditorios
        repo.adicionarEspaco(new Auditorio("Auditorio Principal", 120.0));
        repo.adicionarEspaco(new Auditorio("Auditorio Mirassol", 100.0));
        repo.adicionarEspaco(new Auditorio("Auditorio SP", 110.0));
        repo.adicionarEspaco(new Auditorio("Auditorio Palmeiras", 150.0));

        repo.salvarArquivo("espacos.dat");
        System.out.println("16 espacos criados de todos os tipos");
    }

    private static void gerarReservas() throws Exception {
        RepositorioReservas repo = new RepositorioReservas();

        // Clientes de exemplo
        Cliente cliente1 = new Cliente("12345678909", "Felipe", "felipe@email.com", "(98) 91234-5678", LocalDate.now());
        Cliente cliente2 = new Cliente("23456789012", "Raimundo", "raimundo@email.com", "(99) 92345-6789", LocalDate.now());
        Cliente cliente3 = new Cliente("34567890123", "Messi", "messi@email.com", "(98) 93456-7890", LocalDate.now());
        Cliente cliente4 = new Cliente("45678901234", "Noé", "noe@email.com", "(99) 94567-8901", LocalDate.now());

        // Espaços de exemplo
        Espaco espaco1 = new EstacaoTrabalho("Estacao Individual 1", 15.0);
        Espaco espaco2 = new SalaPrivada("Sala Privada Cr7", 77.7);
        Espaco espaco3 = new SalaReuniao("Sala Reuniao Rai", 50.0);
        Espaco espaco4 = new Auditorio("Auditorio Principal", 120.0);

        // Criando reservas de exemplo
        Reserva reserva1 = new Reserva(cliente1, espaco1,
                LocalDate.now().plusDays(1),
                java.time.LocalTime.of(9, 0),
                java.time.LocalTime.of(12, 0));

        Reserva reserva2 = new Reserva(cliente2, espaco2,
                LocalDate.now().plusDays(2),
                java.time.LocalTime.of(14, 0),
                java.time.LocalTime.of(18, 0));

        Reserva reserva3 = new Reserva(cliente3, espaco3,
                LocalDate.now().plusDays(3),
                java.time.LocalTime.of(8, 0),
                java.time.LocalTime.of(17, 0));

        Reserva reserva4 = new Reserva(cliente4, espaco4,
                LocalDate.now().plusDays(4),
                java.time.LocalTime.of(10, 0),
                java.time.LocalTime.of(16, 0));

        // Adicionando TODOS os tipos de servicos extras nas reservas
        reserva1.adicionarServico(new CafePremium(3, 5.50));
        reserva1.adicionarServico(new Estacionamento(3, 8.0));

        reserva2.adicionarServico(new Locker(1, 10.0));
        reserva2.adicionarServico(new RecebimentoCorrespondencia(2, 3.0));

        reserva3.adicionarServico(new CafePremium(5, 5.50));
        reserva3.adicionarServico(new Estacionamento(9, 8.0));
        reserva3.adicionarServico(new Locker(1, 10.0));

        reserva4.adicionarServico(new CafePremium(2, 5.50));
        reserva4.adicionarServico(new Estacionamento(6, 8.0));
        reserva4.adicionarServico(new Locker(2, 10.0));
        reserva4.adicionarServico(new RecebimentoCorrespondencia(1, 3.0));

        repo.adicionarReserva(reserva1);
        repo.adicionarReserva(reserva2);
        repo.adicionarReserva(reserva3);
        repo.adicionarReserva(reserva4);
        repo.salvarArquivo("reservas.dat");

        System.out.println("4 reservas criadas com todos os tipos de servicos");
    }

}

package controle;

import entidades.Cliente;
import entidades.Espaco;
import entidades.Reserva;
import entidades.ServicoAdicional;
import excecoes.*;
import java.io.Serializable;
import java.util.List;
import java.util.Map;
import java.util.ArrayList;

public class AdministradorSistema implements Serializable {
    private RepositorioClientes repositorioClientes = new RepositorioClientes();
    private RepositorioEspacos repositorioEspacos = new RepositorioEspacos();
    private RepositorioReservas repositorioReservas = new RepositorioReservas();

    public AdministradorSistema() {}

    public void cadastrarCliente(Cliente cliente) throws ClienteJaCadastradoException {
        Cliente clienteExistente = repositorioClientes.buscarCliente(cliente.getCpf());
        if (clienteExistente != null) {
            throw new ClienteJaCadastradoException("Cliente ja cadastrado com este CPF.");
        }
        repositorioClientes.adicionarCliente(cliente);
    }

    public Cliente buscarCliente(String cpf) throws ClienteNaoEncontradoException {
        Cliente cliente = repositorioClientes.buscarCliente(cpf);
        if (cliente == null) {
            throw new ClienteNaoEncontradoException("Cliente nao encontrado.");
        }
        return cliente;
    }

    public void removerCliente(String cpf) throws ClienteNaoEncontradoException {
        Cliente cliente = repositorioClientes.buscarCliente(cpf);
        if (cliente == null) {
            throw new ClienteNaoEncontradoException("Cliente nao encontrado.");
        }
        repositorioClientes.removerCliente(cpf);
    }

    public Map<String, Cliente> listarClientes() {
        return repositorioClientes.listarClientes();
    }

    public void cadastrarEspaco(Espaco espaco) {
        repositorioEspacos.adicionarEspaco(espaco);
    }

    public Espaco buscarEspaco(String id) throws EspacoIndisponivelException {
        Espaco espaco = repositorioEspacos.buscarEspaco(id);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado.");
        }
        return espaco;
    }

    public void removerEspaco(String id) throws EspacoIndisponivelException {
        Espaco espaco = repositorioEspacos.buscarEspaco(id);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado.");
        }
        repositorioEspacos.removerEspaco(id);
    }

    public Map<String, Espaco> listarEspacos() {
        return repositorioEspacos.listarEspacos();
    }

    public void cadastrarReserva(Reserva reserva) throws EspacoIndisponivelException {
        String idEspaco = reserva.getEspaco().getId();
        Espaco espaco = repositorioEspacos.buscarEspaco(idEspaco);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado.");
        }
        repositorioReservas.adicionarReserva(reserva);
    }

    public boolean verificarDisponibilidade(String espacoId, java.time.LocalDate data, java.time.LocalTime inicio, java.time.LocalTime fim) throws EspacoIndisponivelException {
        Espaco espaco = repositorioEspacos.buscarEspaco(espacoId);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado.");
        }
        return true;
    }

    public Reserva buscarReserva(String id) throws ReservaNaoEncontradaException {
        Reserva reserva = repositorioReservas.buscarReserva(id);
        if (reserva == null) {
            throw new ReservaNaoEncontradaException("Reserva nao encontrada.");
        }
        return reserva;
    }

    public void removerReserva(String id) throws ReservaNaoEncontradaException {
        Reserva reserva = repositorioReservas.buscarReserva(id);
        if (reserva == null) {
            throw new ReservaNaoEncontradaException("Reserva nao encontrada.");
        }
        repositorioReservas.removerReserva(id);
    }

    public Map<String, Reserva> listarReservas() {
        return repositorioReservas.listarReservas();
    }

    public void adicionarServicoAReserva(String reservaId, ServicoAdicional servico) throws ReservaNaoEncontradaException, ServicoInvalidoException {
        Reserva reserva = repositorioReservas.buscarReserva(reservaId);
        if (reserva == null) {
            throw new ReservaNaoEncontradaException("Reserva nao encontrada.");
        }
        if (servico == null) {
            throw new ServicoInvalidoException("Servico invalido.");
        }
        reserva.adicionarServico(servico);
    }

    public void salvarTudo() throws FalhaPersistenciaException {
        repositorioClientes.salvarArquivo("clientes.dat");
        repositorioEspacos.salvarArquivo("espacos.dat");
        repositorioReservas.salvarArquivo("reservas.dat");
    }

    public void carregarTudo() throws FalhaPersistenciaException {
        repositorioClientes.carregarArquivo("clientes.dat");
        repositorioEspacos.carregarArquivo("espacos.dat");
        repositorioReservas.carregarArquivo("reservas.dat");
    }

    public List<Reserva> reservasPorCliente(String cpf) {
        List<Reserva> reservasDoCliente = new ArrayList<>();
        Map<String, Reserva> todasReservas = repositorioReservas.listarReservas();
        for (Reserva reserva : todasReservas.values()) {
            if (reserva.getCliente().getCpf().equals(cpf)) {
                reservasDoCliente.add(reserva);
            }
        }

        return reservasDoCliente;
    }
}

package controle;

import entidades.Cliente;
import entidades.Espaco;
import entidades.Reserva;
import entidades.ServicoAdicional;
import excecoes.*;
import java.io.Serializable;
import java.util.List;
import java.util.Map;
import java.util.ArrayList;

public class AdministradorSistema implements Serializable {
    private RepositorioClientes repositorioClientes = new RepositorioClientes();
    private RepositorioEspacos repositorioEspacos = new RepositorioEspacos();
    private RepositorioReservas repositorioReservas = new RepositorioReservas();

    public AdministradorSistema() {}

    public void cadastrarCliente(Cliente cliente) throws ClienteJaCadastradoException {
        Cliente clienteExistente = repositorioClientes.buscarCliente(cliente.getCpf());
        if (clienteExistente != null) {
            throw new ClienteJaCadastradoException("Cliente ja cadastrado com este CPF");
        }
        repositorioClientes.adicionarCliente(cliente);
    }

    public Cliente buscarCliente(String cpf) throws ClienteNaoEncontradoException {
        Cliente cliente = repositorioClientes.buscarCliente(cpf);
        if (cliente == null) {
            throw new ClienteNaoEncontradoException("Cliente nao encontrado");
        }
        return cliente;
    }

    public void removerCliente(String cpf) throws ClienteNaoEncontradoException {
        Cliente cliente = repositorioClientes.buscarCliente(cpf);
        if (cliente == null) {
            throw new ClienteNaoEncontradoException("Cliente nao encontrado");
        }
        repositorioClientes.removerCliente(cpf);
    }

    public Map<String, Cliente> listarClientes() {
        return repositorioClientes.listarClientes();
    }

    public void cadastrarEspaco(Espaco espaco) {
        repositorioEspacos.adicionarEspaco(espaco);
    }

    public Espaco buscarEspaco(String id) throws EspacoIndisponivelException {
        Espaco espaco = repositorioEspacos.buscarEspaco(id);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado");
        }
        return espaco;
    }

    public void removerEspaco(String id) throws EspacoIndisponivelException {
        Espaco espaco = repositorioEspacos.buscarEspaco(id);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado");
        }
        repositorioEspacos.removerEspaco(id);
    }

    public Map<String, Espaco> listarEspacos() {
        return repositorioEspacos.listarEspacos();
    }

    public void cadastrarReserva(Reserva reserva) throws EspacoIndisponivelException {
        String idEspaco = reserva.getEspaco().getId();
        Espaco espaco = repositorioEspacos.buscarEspaco(idEspaco);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado");
        }
        repositorioReservas.adicionarReserva(reserva);
    }

    public boolean verificarDisponibilidade(String espacoId, java.time.LocalDate data, java.time.LocalTime inicio, java.time.LocalTime fim) throws EspacoIndisponivelException {
        Espaco espaco = repositorioEspacos.buscarEspaco(espacoId);
        if (espaco == null) {
            throw new EspacoIndisponivelException("Espaco nao encontrado");
        }
        return true;
    }

    public Reserva buscarReserva(String id) throws ReservaNaoEncontradaException {
        Reserva reserva = repositorioReservas.buscarReserva(id);
        if (reserva == null) {
            throw new ReservaNaoEncontradaException("Reserva nao encontrada");
        }
        return reserva;
    }

    public void removerReserva(String id) throws ReservaNaoEncontradaException {
        Reserva reserva = repositorioReservas.buscarReserva(id);
        if (reserva == null) {
            throw new ReservaNaoEncontradaException("Reserva nao encontrada");
        }
        repositorioReservas.removerReserva(id);
    }

    public Map<String, Reserva> listarReservas() {
        return repositorioReservas.listarReservas();
    }

    public void adicionarServicoAReserva(String reservaId, ServicoAdicional servico) throws ReservaNaoEncontradaException, ServicoInvalidoException {
        Reserva reserva = repositorioReservas.buscarReserva(reservaId);
        if (reserva == null) {
            throw new ReservaNaoEncontradaException("Reserva nao encontrada");
        }
        if (servico == null) {
            throw new ServicoInvalidoException("Servico invalido");
        }
        reserva.adicionarServico(servico);
    }

    public void salvarTudo() throws FalhaPersistenciaException {
        repositorioClientes.salvarArquivo("clientes.dat");
        repositorioEspacos.salvarArquivo("espacos.dat");
        repositorioReservas.salvarArquivo("reservas.dat");
    }

    public void carregarTudo() throws FalhaPersistenciaException {
        repositorioClientes.carregarArquivo("clientes.dat");
        repositorioEspacos.carregarArquivo("espacos.dat");
        repositorioReservas.carregarArquivo("reservas.dat");
    }

    public List<Reserva> reservasPorCliente(String cpf) {
        List<Reserva> reservasDoCliente = new ArrayList<>();
        Map<String, Reserva> todasReservas = repositorioReservas.listarReservas();
        for (Reserva reserva : todasReservas.values()) {
            if (reserva.getCliente().getCpf().equals(cpf)) {
                reservasDoCliente.add(reserva);
            }
        }

        return reservasDoCliente;
    }
}

package controle;

import entidades.Cliente;
import excecoes.FalhaPersistenciaException;
import java.io.*;
import java.util.HashMap;
import java.util.Map;

/**
 * Esta classe e como um arquivo de clientes
 * Guarda todos os clientes cadastrados no sistema
 */
public class RepositorioClientes implements Serializable {
    // HashMap e como uma agenda telefonica - busca pelo CPF
    private Map<String, Cliente> clientes = new HashMap<>();

    /**
     * Adiciona um novo cliente na "agenda"
     * O CPF e como se fosse o numero da pagina
     */
    public void adicionarCliente(Cliente cliente) {
        clientes.put(cliente.getCpf(), cliente);
    }

    /**
     * Procura um cliente pelo CPF
     * Como procurar alguem na agenda pelo telefone
     */
    public Cliente buscarCliente(String cpf) {
        return clientes.get(cpf);
    }

    /**
     * Remove um cliente da "agenda"
     */
    public void removerCliente(String cpf) {
        clientes.remove(cpf);
    }

    /**
     * Mostra todos os clientes cadastrados
     * Como ver a agenda inteira
     */
    public Map<String, Cliente> listarClientes() {
        return clientes;
    }

    /**
     * Salva todos os clientes em um arquivo no computador
     * Como fazer backup da agenda no pen drive
     */
    public void salvarArquivo(String caminho) throws FalhaPersistenciaException {
        try {
            // Abre o arquivo para escrever
            FileOutputStream arquivo = new FileOutputStream(caminho);
            ObjectOutputStream gravador = new ObjectOutputStream(arquivo);

            // Escreve todos os clientes no arquivo
            gravador.writeObject(clientes);

            // Fecha o arquivo
            gravador.close();
            arquivo.close();

        } catch (IOException e) {
            throw new FalhaPersistenciaException("Erro ao salvar clientes: " + e.getMessage());
        }
    }

    /**
     * Carrega os clientes de um arquivo do computador
     * Como restaurar a agenda do pen drive
     */
    public void carregarArquivo(String caminho) throws FalhaPersistenciaException {
        try {
            // Abre o arquivo para ler
            FileInputStream arquivo = new FileInputStream(caminho);
            ObjectInputStream leitor = new ObjectInputStream(arquivo);

            // Le todos os clientes do arquivo
            clientes = (Map<String, Cliente>) leitor.readObject();

            // Fecha o arquivo
            leitor.close();
            arquivo.close();

        } catch (FileNotFoundException e) {
            // Se o arquivo nao existe, cria uma agenda vazia
            clientes = new HashMap<>();
        } catch (IOException | ClassNotFoundException e) {
            throw new FalhaPersistenciaException("Erro ao carregar clientes: " + e.getMessage());
        }
    }
}

package controle;

import entidades.Cliente;
import excecoes.FalhaPersistenciaException;
import java.io.*;
import java.util.HashMap;
import java.util.Map;

public class RepositorioClientes implements Serializable {
    private Map<String, Cliente> clientes = new HashMap<>();

    public void adicionarCliente(Cliente cliente) {
        clientes.put(cliente.getCpf(), cliente);
    }

    public Cliente buscarCliente(String cpf) {
        return clientes.get(cpf);
    }

    public void removerCliente(String cpf) {
        clientes.remove(cpf);
    }

    public Map<String, Cliente> listarClientes() {
        return clientes;
    }

    public void salvarArquivo(String caminho) throws FalhaPersistenciaException {
        try {
            FileOutputStream arquivo = new FileOutputStream(caminho);
            ObjectOutputStream gravador = new ObjectOutputStream(arquivo);
            gravador.writeObject(clientes);
            gravador.close();
            arquivo.close();
        } catch (IOException e) {
            throw new FalhaPersistenciaException("Erro ao salvar clientes: " + e.getMessage());
        }
    }

    public void carregarArquivo(String caminho) throws FalhaPersistenciaException {
        try {
            FileInputStream arquivo = new FileInputStream(caminho);
            ObjectInputStream leitor = new ObjectInputStream(arquivo);
            clientes = (Map<String, Cliente>) leitor.readObject();
            leitor.close();
            arquivo.close();
        } catch (FileNotFoundException e) {
            clientes = new HashMap<>();
        } catch (IOException | ClassNotFoundException e) {
            throw new FalhaPersistenciaException("Erro ao carregar clientes: " + e.getMessage());
        }
    }
}

