package entidades;

import java.io.Serializable;
import java.time.LocalDate;
import java.time.LocalTime;
import java.util.ArrayList;
import java.util.List;

public class Reserva implements Serializable {
    private String id;
    private Cliente cliente;
    private Espaco espaco;
    private LocalDate data;
    private LocalTime horaInicio;
    private LocalTime horaFim;
    private List<ServicoAdicional> servicosAdicionais;

    public Reserva(Cliente cliente, Espaco espaco, LocalDate data, LocalTime horaInicio, LocalTime horaFim) {
        // Cria um ID unico usando o tempo atual
        this.id = "RE" + System.currentTimeMillis();
        this.cliente = cliente;
        this.espaco = espaco;
        this.data = data;
        this.horaInicio = horaInicio;
        this.horaFim = horaFim;
        // Cria uma lista vazia para os servicos extras
        this.servicosAdicionais = new ArrayList<>();
    }

    public String getId() {
        return id;
    }

    public Cliente getCliente() {
        return cliente;
    }

    public void setCliente(Cliente cliente) {
        this.cliente = cliente;
    }

    public Espaco getEspaco() {
        return espaco;
    }

    public void setEspaco(Espaco espaco) {
        this.espaco = espaco;
    }

    public LocalDate getData() {
        return data;
    }

    public void setData(LocalDate data) {
        this.data = data;
    }

    public LocalTime getHoraInicio() {
        return horaInicio;
    }

    public void setHoraInicio(LocalTime horaInicio) {
        this.horaInicio = horaInicio;
    }

    public LocalTime getHoraFim() {
        return horaFim;
    }

    public void setHoraFim(LocalTime horaFim) {
        this.horaFim = horaFim;
    }

    public List<ServicoAdicional> getServicosAdicionais() {
        return servicosAdicionais;
    }

    // ========== METODOS PARA SERVICOS EXTRAS ==========

    public void adicionarServico(ServicoAdicional servico) {
        this.servicosAdicionais.add(servico);
    }

    public void removerServico(ServicoAdicional servico) {
        this.servicosAdicionais.remove(servico);
    }

    // ========== CALCULO DO VALOR TOTAL ==========

    /**
     * Calcula quanto a pessoa vai pagar no total
     * Soma o valor do espaco + servicos extras
     */
    public double calcularValorTotal() {
        // Primeiro calcula quantas horas a pessoa vai usar o espaco
        int horasReservadas = calcularHorasDeUso();

        // Multiplica as horas pelo valor por hora do espaco
        double valorEspaco = horasReservadas * espaco.getValorHora();

        // Soma o valor de todos os servicos extras
        double valorServicos = calcularValorServicos();

        // Valor total = espaco + servicos
        return valorEspaco + valorServicos;
    }

    /**
     * Calcula quantas horas a pessoa vai usar o espaco
     * Metodo mais simples que um iniciante entenderia
     */
    private int calcularHorasDeUso() {
        // Pega a hora de inicio (ex: 9)
        int horaInicial = horaInicio.getHour();

        // Pega a hora de fim (ex: 12)
        int horaFinal = horaFim.getHour();

        // Calcula a diferenca (ex: 12 - 9 = 3 horas)
        int horas = horaFinal - horaInicial;

        // Se der numero negativo ou zero, considera pelo menos 1 hora
        if (horas <= 0) {
            horas = 1;
        }

        return horas;
    }

    /**
     * Soma o valor de todos os servicos extras
     */
    private double calcularValorServicos() {
        double total = 0.0;

        // Percorre todos os servicos e soma os valores
        for (ServicoAdicional servico : servicosAdicionais) {
            total = total + servico.getValorTotal();
        }

        return total;
    }

    /**
     * Mostra as informacoes da reserva de forma organizada
     */
    @Override
    public String toString() {
        return "Reserva{" +
                "id='" + id + '\'' +
                ", cliente=" + cliente.getNome() +
                ", espaco=" + espaco.getNome() +
                ", data=" + data +
                ", horaInicio=" + horaInicio +
                ", horaFim=" + horaFim +
                ", valorTotal=" + calcularValorTotal() +
                '}';
    }
}

package entidades;

import java.io.Serializable;
import java.time.LocalDate;
import java.time.LocalTime;
import java.util.ArrayList;
import java.util.List;

public class Reserva implements Serializable {
    private String id;
    private Cliente cliente;
    private Espaco espaco;
    private LocalDate data;
    private LocalTime horaInicio;
    private LocalTime horaFim;
    private List<ServicoAdicional> servicosAdicionais;

    public Reserva(Cliente cliente, Espaco espaco, LocalDate data, LocalTime horaInicio, LocalTime horaFim) {
        this.id = "RE" + System.currentTimeMillis();
        this.cliente = cliente;
        this.espaco = espaco;
        this.data = data;
        this.horaInicio = horaInicio;
        this.horaFim = horaFim;
        this.servicosAdicionais = new ArrayList<>();
    }

    public String getId() {
        return id;
    }

    public Cliente getCliente() {
        return cliente;
    }

    public void setCliente(Cliente cliente) {
        this.cliente = cliente;
    }

    public Espaco getEspaco() {
        return espaco;
    }

    public void setEspaco(Espaco espaco) {
        this.espaco = espaco;
    }

    public LocalDate getData() {
        return data;
    }

    public void setData(LocalDate data) {
        this.data = data;
    }

    public LocalTime getHoraInicio() {
        return horaInicio;
    }

    public void setHoraInicio(LocalTime horaInicio) {
        this.horaInicio = horaInicio;
    }

    public LocalTime getHoraFim() {
        return horaFim;
    }

    public void setHoraFim(LocalTime horaFim) {
        this.horaFim = horaFim;
    }

    public List<ServicoAdicional> getServicosAdicionais() {
        return servicosAdicionais;
    }

    public void adicionarServico(ServicoAdicional servico) {
        this.servicosAdicionais.add(servico);
    }

    public void removerServico(ServicoAdicional servico) {
        this.servicosAdicionais.remove(servico);
    }

    public double calcularValorTotal() {
        int horasReservadas = calcularHorasDeUso();
        double valorEspaco = horasReservadas * espaco.getValorHora();
        double valorServicos = calcularValorServicos();
        return valorEspaco + valorServicos;
    }

    private int calcularHorasDeUso() {
        int horaInicial = horaInicio.getHour();
        int horaFinal = horaFim.getHour();
        int horas = horaFinal - horaInicial;
        if (horas <= 0) {
            horas = 1;
        }
        return horas;
    }

    private double calcularValorServicos() {
        double total = 0.0;
        for (ServicoAdicional servico : servicosAdicionais) {
            total = total + servico.getValorTotal();
        }
        return total;
    }

    @Override
    public String toString() {
        return "Reserva{" +
                "id='" + id + '\'' +
                ", cliente=" + cliente.getNome() +
                ", espaco=" + espaco.getNome() +
                ", data=" + data +
                ", horaInicio=" + horaInicio +
                ", horaFim=" + horaFim +
                ", valorTotal=" + calcularValorTotal() +
                '}';
    }
}

package fronteira;

import controle.AdministradorSistema;
import entidades.Cliente;
import excecoes.ClienteJaCadastradoException;
import excecoes.ClienteNaoEncontradoException;
import java.time.LocalDate;
import java.util.Map;
import java.util.Scanner;

public class MenuClientes {
    private AdministradorSistema admin;
    private Scanner scanner;

    public MenuClientes(AdministradorSistema admin, Scanner scanner) {
        this.admin = admin;
        this.scanner = scanner;
    }

    public void exibirMenu() {
        int opcao = -1;
        while (opcao != 0) {
            mostrarOpcoes();
            opcao = lerOpcaoDoUsuario();
            executarOpcao(opcao);
        }
    }

    private void mostrarOpcoes() {
        System.out.println("\n=== MENU DE CLIENTES ===");
        System.out.println("1. Ver todos os clientes cadastrados");
        System.out.println("2. Cadastrar um novo cliente");
        System.out.println("3. Buscar cliente por CPF");
        System.out.println("4. Remover um cliente");
        System.out.println("0. Voltar ao menu principal");
        System.out.print("Digite sua opcao: ");
    }

    private int lerOpcaoDoUsuario() {
        try {
            return Integer.parseInt(scanner.nextLine());
        } catch (Exception e) {
            return -1;
        }
    }

    private void executarOpcao(int opcao) {
        switch (opcao) {
            case 1:
                listarTodosClientes();
                break;
            case 2:
                cadastrarNovoCliente();
                break;
            case 3:
                buscarClientePorCpf();
                break;
            case 4:
                removerCliente();
                break;
            case 0:
                System.out.println("Voltando ao menu principal...");
                break;
            default:
                System.out.println("Opcao invalida! Digite um numero de 0 a 4.");
        }
    }

    private void listarTodosClientes() {
        Map<String, Cliente> clientes = admin.listarClientes();
        if (clientes.isEmpty()) {
            System.out.println("Nenhum cliente cadastrado ainda.");
            return;
        }
        System.out.println("\n=== LISTA DE CLIENTES ===");
        for (Cliente cliente : clientes.values()) {
            mostrarDadosCliente(cliente);
            System.out.println("------------------------");
        }
    }

    private void cadastrarNovoCliente() {
        System.out.println("\n=== CADASTRAR NOVO CLIENTE ===");
        try {
            System.out.print("Digite o CPF (11 numeros): ");
            String cpf = scanner.nextLine();
            if (!validarCpf(cpf)) {
                System.out.println("CPF invalido! Deve ter exatamente 11 numeros.");
                return;
            }
            System.out.print("Digite o nome: ");
            String nome = scanner.nextLine();
            System.out.print("Digite o email: ");
            String email = scanner.nextLine();
            System.out.print("Digite o telefone: ");
            String telefone = scanner.nextLine();
            LocalDate hoje = LocalDate.now();
            Cliente novoCliente = new Cliente(cpf, nome, email, telefone, hoje);
            admin.cadastrarCliente(novoCliente);
            System.out.println("Cliente cadastrado com sucesso!");
        } catch (ClienteJaCadastradoException e) {
            System.out.println("Erro: " + e.getMessage());
        } catch (Exception e) {
            System.out.println("Erro inesperado: " + e.getMessage());
        }
    }

    private void buscarClientePorCpf() {
        System.out.println("\n=== BUSCAR CLIENTE ===");
        System.out.print("Digite o CPF do cliente: ");
        String cpf = scanner.nextLine();
        try {
            Cliente cliente = admin.buscarCliente(cpf);
            System.out.println("\nCliente encontrado:");
            mostrarDadosCliente(cliente);
        } catch (ClienteNaoEncontradoException e) {
            System.out.println("Erro: " + e.getMessage());
        }
    }

    private void removerCliente() {
        System.out.println("\n=== REMOVER CLIENTE ===");
        System.out.print("Digite o CPF do cliente a ser removido: ");
        String cpf = scanner.nextLine();
        try {
            Cliente cliente = admin.buscarCliente(cpf);
            System.out.println("\nCliente a ser removido:");
            mostrarDadosCliente(cliente);
            System.out.print("Tem certeza? (digite 'SIM' para confirmar): ");
            String confirmacao = scanner.nextLine();
            if (confirmacao.equalsIgnoreCase("SIM")) {
                admin.removerCliente(cpf);
                System.out.println("Cliente removido com sucesso!");
            } else {
                System.out.println("Remocao cancelada.");
            }
        } catch (ClienteNaoEncontradoException e) {
            System.out.println("Erro: " + e.getMessage());
        }
    }

    private void mostrarDadosCliente(Cliente cliente) {
        System.out.println("CPF: " + cliente.getCpf());
        System.out.println("Nome: " + cliente.getNome());
        System.out.println("Email: " + cliente.getEmail());
        System.out.println("Telefone: " + cliente.getTelefone());
        System.out.println("Data de cadastro: " + cliente.getDataCadastro());
    }

    private boolean validarCpf(String cpf) {
        cpf = cpf.replaceAll("[^0-9]", "");
        return cpf.length() == 11;
    }
}

package fronteira;

import controle.AdministradorSistema;
import entidades.Cliente;
import excecoes.ClienteJaCadastradoException;
