    }

    private void executarOpcao(int opcao) {
        switch (opcao) {
            case 1:
                listarTodosClientes();
                break;
            case 2:
                cadastrarNovoCliente();
                break;
            case 3:
                buscarClientePorCpf();
                break;
            case 4:
                removerCliente();
                break;
            case 0:
                System.out.println("Voltando ao menu principal");
                break;
            default:
                System.out.println("Opcao invalida Digite um numero de 0 a 4");
        }
    }

    private void listarTodosClientes() {
        Map<String, Cliente> clientes = admin.listarClientes();
        if (clientes.isEmpty()) {
            System.out.println("Nenhum cliente cadastrado ainda");
            return;
        }
        System.out.println("\n=== LISTA DE CLIENTES ===");
        for (Cliente cliente : clientes.values()) {
            mostrarDadosCliente(cliente);
            System.out.println("------------------------");
        }
    }

    private void cadastrarNovoCliente() {
        System.out.println("\n=== CADASTRAR NOVO CLIENTE ===");
        try {
            System.out.print("Digite o CPF (11 numeros): ");
            String cpf = scanner.nextLine();
            if (!validarCpf(cpf)) {
                System.out.println("CPF invalido Deve ter exatamente 11 numeros");
                return;
            }
            System.out.print("Digite o nome: ");
            String nome = scanner.nextLine();
            System.out.print("Digite o email: ");
            String email = scanner.nextLine();
            System.out.print("Digite o telefone: ");
            String telefone = scanner.nextLine();
            LocalDate hoje = LocalDate.now();
            Cliente novoCliente = new Cliente(cpf, nome, email, telefone, hoje);
            admin.cadastrarCliente(novoCliente);
            System.out.println("Cliente cadastrado com sucesso");
        } catch (ClienteJaCadastradoException e) {
            System.out.println("Erro: " + e.getMessage());
        } catch (Exception e) {
            System.out.println("Erro inesperado: " + e.getMessage());
        }
    }

    private void buscarClientePorCpf() {
        System.out.println("\n=== BUSCAR CLIENTE ===");
        System.out.print("Digite o CPF do cliente: ");
        String cpf = scanner.nextLine();
        try {
            Cliente cliente = admin.buscarCliente(cpf);
            System.out.println("\nCliente encontrado:");
            mostrarDadosCliente(cliente);
        } catch (ClienteNaoEncontradoException e) {
            System.out.println("Erro: " + e.getMessage());
        }
    }

    private void removerCliente() {
        System.out.println("\n=== REMOVER CLIENTE ===");
        System.out.print("Digite o CPF do cliente a ser removido: ");
        String cpf = scanner.nextLine();
        try {
            Cliente cliente = admin.buscarCliente(cpf);
            System.out.println("\nCliente a ser removido:");
            mostrarDadosCliente(cliente);
            System.out.print("Tem certeza (digite 'SIM' para confirmar): ");
            String confirmacao = scanner.nextLine();
            if (confirmacao.equalsIgnoreCase("SIM")) {
                admin.removerCliente(cpf);
                System.out.println("Cliente removido com sucesso");
            } else {
                System.out.println("Remocao cancelada");
            }
        } catch (ClienteNaoEncontradoException e) {
            System.out.println("Erro: " + e.getMessage());
        }
    }

    private void mostrarDadosCliente(Cliente cliente) {
        System.out.println("CPF: " + cliente.getCpf());
        System.out.println("Nome: " + cliente.getNome());
        System.out.println("Email: " + cliente.getEmail());
        System.out.println("Telefone: " + cliente.getTelefone());
        System.out.println("Data de cadastro: " + cliente.getDataCadastro());
    }

    private boolean validarCpf(String cpf) {
        cpf = cpf.replaceAll("[^0-9]", "");
        return cpf.length() == 11;
    }
}

package fronteira;

import controle.AdministradorSistema;
import java.util.Scanner;

public class MenuPrincipal {
    private AdministradorSistema admin;
    private Scanner scanner;

    public MenuPrincipal(AdministradorSistema admin) {
        this.admin = admin;
        this.scanner = new Scanner(System.in);
    }

    public void exibirMenu() {
        int opcao = -1;

        while (opcao != 0) {
            mostrarOpcoes();
            opcao = lerOpcaoDoUsuario();
            executarOpcao(opcao);
        }
    }

    private void mostrarOpcoes() {
        System.out.println("\n=== WorkHub - Sistema de Gestao de Coworking ===");
        System.out.println("1. Gerenciar Clientes");
        System.out.println("2. Gerenciar Espacos");
        System.out.println("3. Gerenciar Reservas");
        System.out.println("4. Ver Relatorios");
        System.out.println("0. Sair do sistema");
        System.out.print("Digite sua opcao: ");
    }

    private int lerOpcaoDoUsuario() {
        try {
            return Integer.parseInt(scanner.nextLine());
        } catch (Exception e) {
            return -1;
        }
    }

    private void executarOpcao(int opcao) {
        switch (opcao) {
            case 1:
                abrirMenuClientes();
                break;
            case 2:
                abrirMenuEspacos();
                break;
            case 3:
                abrirMenuReservas();
                break;
            case 4:
                abrirMenuRelatorios();
                break;
            case 0:
                System.out.println("Saindo do sistema. Ate logo!");
                break;
            default:
                System.out.println("Opcao invalida! Digite um numero de 0 a 4.");
        }
    }

    private void abrirMenuClientes() {
        MenuClientes menuClientes = new MenuClientes(admin, scanner);
        menuClientes.exibirMenu();
    }

    private void abrirMenuEspacos() {
        MenuEspacos menuEspacos = new MenuEspacos(admin, scanner);
        menuEspacos.exibirMenu();
    }

    private void abrirMenuReservas() {
        MenuReservas menuReservas = new MenuReservas(admin, scanner);
        menuReservas.exibirMenu();
    }

    private void abrirMenuRelatorios() {
        MenuRelatorios menuRelatorios = new MenuRelatorios(admin, scanner);
        menuRelatorios.exibirMenu();
    }
}

