package fronteira;

import controle.AdministradorSistema;
import entidades.Reserva;
import entidades.Espaco;
import entidades.Cliente;
import entidades.ServicoAdicional;
import java.util.List;
import java.util.Map;
import java.util.Scanner;

public class MenuRelatorios {
    private AdministradorSistema admin;
    private Scanner scanner;

    public MenuRelatorios(AdministradorSistema admin, Scanner scanner) {
        this.admin = admin;
        this.scanner = scanner;
    }

    public void exibirMenu() {
        int opcao = -1;
        while (opcao != 0) {
            System.out.println("\nMenu de Relatorios");
            System.out.println("1. Reservas por Cliente");
            System.out.println("2. Utilizacao de Espacos");
            System.out.println("3. Faturamento");
            System.out.println("4. Servicos Adicionais");
            System.out.println("0. Voltar");
            System.out.print("Escolha uma opcao: ");
            try {
                opcao = Integer.parseInt(scanner.nextLine());
            } catch (Exception e) {
                opcao = -1;
            }
            switch (opcao) {
                case 1:
                    relatorioReservasPorCliente();
                    break;
                case 2:
                    relatorioUtilizacaoEspacos();
                    break;
                case 3:
                    relatorioFaturamento();
                    break;
                case 4:
                    relatorioServicosAdicionais();
                    break;
                case 0:
                    System.out.println("Voltando");
                    break;
                default:
                    System.out.println("Opcao invalida");
            }
        }
    }

    private void relatorioReservasPorCliente() {
        System.out.print("Digite o CPF do cliente: ");
        String cpf = scanner.nextLine();
        List<Reserva> reservas = admin.reservasPorCliente(cpf);
        if (reservas.isEmpty()) {
            System.out.println("Nenhuma reserva encontrada para este cliente");
        } else {
            for (Reserva r : reservas) {
                System.out.println("Reserva ID: " + r.getId() + " | Espaco: " + r.getEspaco().getNome() + " | Data: " + r.getDataReserva());
            }
        }
    }

    private void relatorioUtilizacaoEspacos() {
        Map<String, Espaco> espacos = admin.listarEspacos();
        Map<Integer, Reserva> reservas = admin.listarReservas();
        for (Espaco e : espacos.values()) {
            int totalReservas = 0;
            for (Reserva r : reservas.values()) {
                if (r.getEspaco().getId().equals(e.getId())) {
                    totalReservas++;
                }
            }
            System.out.println(e.getNome() + " - Reservas: " + totalReservas);
        }
    }

    private void relatorioFaturamento() {
        Map<Integer, Reserva> reservas = admin.listarReservas();
        double total = 0;
        for (Reserva r : reservas.values()) {
            total += r.getValorTotal();
        }
        System.out.println("Faturamento total: R$ " + total);
    }

    private void relatorioServicosAdicionais() {
        Map<Integer, Reserva> reservas = admin.listarReservas();
        int totalServicos = 0;
        double valorServicos = 0;
        for (Reserva r : reservas.values()) {
            for (ServicoAdicional s : r.getServicosAdicionais()) {
                totalServicos++;
                valorServicos += s.getValorTotal();
            }
        }
        System.out.println("Total de servicos adicionais: " + totalServicos);
        System.out.println("Valor arrecadado com servicos: R$ " + valorServicos);
    }
}

package fronteira;

import controle.AdministradorSistema;
import entidades.Reserva;
import entidades.Espaco;
import entidades.Cliente;
import entidades.ServicoAdicional;
import java.util.List;
import java.util.Map;
import java.util.Scanner;

public class MenuRelatorios {
    private AdministradorSistema admin;
    private Scanner scanner;

    public MenuRelatorios(AdministradorSistema admin, Scanner scanner) {
        this.admin = admin;
        this.scanner = scanner;
    }

    public void exibirMenu() {
        int opcao = -1;
        while (opcao != 0) {
            System.out.println("\nMenu de Relatorios");
            System.out.println("1. Reservas por Cliente");
            System.out.println("2. Utilizacao de Espacos");
            System.out.println("3. Faturamento");
            System.out.println("4. Servicos Adicionais");
            System.out.println("0. Voltar");
            System.out.print("Escolha uma opcao: ");
            try {
                opcao = Integer.parseInt(scanner.nextLine());
            } catch (Exception e) {
                opcao = -1;
            }
            switch (opcao) {
                case 1:
                    relatorioReservasPorCliente();
                    break;
                case 2:
                    relatorioUtilizacaoEspacos();
                    break;
                case 3:
                    relatorioFaturamento();
                    break;
                case 4:
                    relatorioServicosAdicionais();
                    break;
                case 0:
                    System.out.println("Voltando");
                    break;
                default:
                    System.out.println("Opcao invalida");
            }
        }
    }

    private void relatorioReservasPorCliente() {
        System.out.print("Digite o CPF do cliente: ");
        String cpf = scanner.nextLine();
        List<Reserva> reservas = admin.reservasPorCliente(cpf);
        if (reservas.isEmpty()) {
            System.out.println("Nenhuma reserva encontrada para este cliente");
        } else {
            for (Reserva r : reservas) {
                System.out.println("Reserva ID: " + r.getId() + " | Espaco: " + r.getEspaco().getNome() + " | Data: " + r.getData());
            }
        }
    }

    private void relatorioUtilizacaoEspacos() {
        Map<String, Espaco> espacos = admin.listarEspacos();
        Map<String, Reserva> reservas = admin.listarReservas();
        for (Espaco e : espacos.values()) {
            int totalReservas = 0;
            for (Reserva r : reservas.values()) {
                if (r.getEspaco().getId().equals(e.getId())) {
                    totalReservas++;
                }
            }
            System.out.println(e.getNome() + " - Reservas: " + totalReservas);
        }
    }

    private void relatorioFaturamento() {
        Map<String, Reserva> reservas = admin.listarReservas();
        double total = 0;
        for (Reserva r : reservas.values()) {
            total += r.calcularValorTotal();
        }
        System.out.println("Faturamento total: R$ " + total);
    }

    private void relatorioServicosAdicionais() {
        Map<String, Reserva> reservas = admin.listarReservas();
        int totalServicos = 0;
        double valorServicos = 0;
        for (Reserva r : reservas.values()) {
            for (ServicoAdicional s : r.getServicosAdicionais()) {
                totalServicos++;
                valorServicos += s.getValorTotal();
            }
        }
        System.out.println("Total de servicos adicionais: " + totalServicos);
        System.out.println("Valor arrecadado com servicos: R$ " + valorServicos);
    }
}

package fronteira;

import controle.AdministradorSistema;
import entidades.Reserva;
import entidades.Cliente;
import entidades.Espaco;
import excecoes.*;
import java.util.Map;
import java.util.Scanner;

public class MenuReservas {
    private AdministradorSistema admin;
    private Scanner scanner;

    public MenuReservas(AdministradorSistema admin, Scanner scanner) {
        this.admin = admin;
        this.scanner = scanner;
    }

    public void exibirMenu() {
        int opcao = -1;
        while (opcao != 0) {
            System.out.println("\n  Menu de Reservas");
            System.out.println("1. Listar reservas");
            System.out.println("2. Cadastrar reserva");
            System.out.println("3. Remover reserva");
            System.out.println("0. Voltar");
            System.out.print("Escolha uma opção: ");
            try {
                opcao = Integer.parseInt(scanner.nextLine());
            } catch (Exception e) {
                opcao = -1;
            }
            switch (opcao) {
                case 1:
                    listarReservas();
                    break;
                case 2:
                    cadastrarReserva();
                    break;
                case 3:
                    removerReserva();
                    break;
                case 0:
                    System.out.println("Voltando");
                    break;
                default:
                    System.out.println("Opção invalida!");
            }
        }
    }

    private void listarReservas() {
        Map<String, Reserva> reservas = admin.listarReservas();
        if (reservas.isEmpty()) {
            System.out.println("Nenhuma reserva encontrada");
            return;
        }
        System.out.println("\n--- RESERVAS ---");
        for (Reserva reserva : reservas.values()) {
            System.out.println("ID: " + reserva.getId());
            System.out.println("Cliente: " + reserva.getCliente().getNome());
            System.out.println("Espaco: " + reserva.getEspaco().getNome());
            System.out.println("Data: " + reserva.getData());
            System.out.println("Hora: " + reserva.getHoraInicio() + " - " + reserva.getHoraFim());
            System.out.println("Valor: R$ " + reserva.calcularValorTotal());
            System.out.println("----------------------");
        }
    }

    private void cadastrarReserva() {
        try {
            System.out.print("Digite o CPF do cliente: ");
            String cpf = scanner.nextLine();
            Cliente cliente = admin.buscarCliente(cpf);

            System.out.print("Digite o ID do espaco: ");
            String espacoId = scanner.nextLine();
            Espaco espaco = admin.buscarEspaco(espacoId);

            System.out.print("Digite a data da reserva (yyyy-mm-dd): ");
            String data = scanner.nextLine();
            java.time.LocalDate dataReserva = java.time.LocalDate.parse(data);

            System.out.print("Digite a hora de inicio (HH:mm): ");
            String horaInicio = scanner.nextLine();
            java.time.LocalTime inicio = java.time.LocalTime.parse(horaInicio);

            System.out.print("Digite a hora de fim (HH:mm): ");
            String horaFim = scanner.nextLine();
            java.time.LocalTime fim = java.time.LocalTime.parse(horaFim);

            Reserva reserva = new Reserva(cliente, espaco, dataReserva, inicio, fim);
            admin.cadastrarReserva(reserva);
            System.out.println("Reserva cadastrada");
        } catch (Exception e) {
            System.out.println("Erro ao cadastrar reserva: " + e.getMessage());
        }
    }

    private void removerReserva() {
        System.out.print("Digite o id da reserva para remover: ");
        String id = scanner.nextLine();
        try {
            admin.removerReserva(id);
            System.out.println("Reserva removida");
        } catch (ReservaNaoEncontradaException e) {
            System.out.println(e.getMessage());
        }
    }
}

package fronteira;

import controle.AdministradorSistema;
